import React from 'react';

const Shop = () => {
  const products = [
    {
      image: '/assets/image/hero-photo.png', // Corrected image path
      title: "RWK Black T-Shirt",
      buyLink: "https://buy.stripe.com/test_aEU2aPfradAv9ck5kk"
    },
    {
      image: '/assets/image/products/RWK-tshirt--black.png', // Corrected image path
      title: "RWK Black T-Shirt",
      buyLink: "https://buy.stripe.com/test_aEU2aPfradAv9ck5kk"
    },
    {
      image: '/assets/image/products/RWK-tshirt--black.png', // Corrected image path
      title: "RWK Black T-Shirt",
      buyLink: "https://buy.stripe.com/test_aEU2aPfradAv9ck5kk"
    },
    {
      image: '/assets/image/products/RWK-tshirt--black.png', // Corrected image path
      title: "RWK Black T-Shirt",
      buyLink: "https://buy.stripe.com/test_aEU2aPfradAv9ck5kk"
    }
  ];

  return (
    <section className="shop">
      <div className="main-container main-container--shop">
        <h2>Our Shop</h2>
        <div className="shop__container">
          {products.map((product, index) => (
            <div key={index} className="shop__product">
              <img 
                className="shop__image" 
                src={product.image} 
                alt={product.title} 
              />
              <p className="shop__title">{product.title}</p>
              <a href={product.buyLink} target="_blank" rel="noopener noreferrer">
                <button className="button">Buy</button>
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Shop;
