import React, { useState } from 'react';
import { ShoppingCart, X } from 'lucide-react';

/* --- Custom UI Components --- */

// A simple Button component
const Button = ({ children, className = "", onClick, ...props }) => {
  return (
    <button
      onClick={onClick}
      className={`px-4 py-2 border rounded hover:bg-gray-100 ${className}`}
      {...props}
    >
      {children}
    </button>
  );
};

// A simple Card component
const Card = ({ children, className = "" }) => {
  return (
    <div className={`shadow p-4 border rounded ${className}`}>
      {children}
    </div>
  );
};

// A simple Sheet component to act as a modal/panel
const Sheet = ({ open, onOpenChange, children }) => {
  return (
    <>
      {children}
      {open && (
        // An overlay to close the sheet when clicking outside
        <div
          className="fixed inset-0 bg-black opacity-50"
          onClick={() => onOpenChange(false)}
        />
      )}
    </>
  );
};

// A component that wraps a trigger element
const SheetTrigger = ({ asChild, children }) => {
  // We assume children is a single element that handles its own click event.
  return children;
};

// The content of the sheet displayed as a modal
const SheetContent = ({ children }) => {
  return (
    <div className="fixed top-0 right-0 bottom-0 w-80 bg-white shadow-lg p-4 overflow-auto z-50">
      {children}
    </div>
  );
};

const SheetHeader = ({ children }) => {
  return <div className="mb-4 border-b pb-2">{children}</div>;
};

const SheetTitle = ({ children }) => {
  return <h2 className="text-xl font-bold">{children}</h2>;
};

/* --- Main ShoppingCartComponent --- */

const ShoppingCartComponent = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [cart, setCart] = useState([]);
  
  // Sample products - replace with your actual products
  const products = [
    { id: 1, name: "Product 1", price: 19.99 },
    { id: 2, name: "Product 2", price: 29.99 }
  ];

  const addToCart = (product) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity < 1) return;
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId 
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
    try {
      // Replace with your actual Stripe publishable key
      const stripe = window.Stripe('your_publishable_key');
      
      // Replace with your actual backend endpoint
      const response = await fetch('/api/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ items: cart }),
      });
      
      const session = await response.json();
      
      // Redirect to Stripe Checkout
      const result = await stripe.redirectToCheckout({
        sessionId: session.id,
      });

      if (result.error) {
        console.error(result.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="relative p-4">
      <Sheet open={isOpen} onOpenChange={setIsOpen}>
        <SheetTrigger asChild>
          <Button className="relative" onClick={() => setIsOpen(true)}>
            <ShoppingCart className="h-5 w-5" />
            {cart.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-blue-500 text-white rounded-full w-5 h-5 text-xs flex items-center justify-center">
                {cart.length}
              </span>
            )}
          </Button>
        </SheetTrigger>
        {isOpen && (
          <SheetContent>
            <SheetHeader>
              <SheetTitle>Shopping Cart</SheetTitle>
            </SheetHeader>
            
            <div className="mt-4 space-y-4">
              {cart.map(item => (
                <Card key={item.id}>
                  <div className="flex justify-between items-center">
                    <div>
                      <h3 className="font-medium">{item.name}</h3>
                      <p className="text-sm text-gray-500">${item.price}</p>
                    </div>
                    <div className="flex items-center gap-2">
                      <Button 
                        className="px-2 py-1"
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      >
                        -
                      </Button>
                      <span>{item.quantity}</span>
                      <Button 
                        className="px-2 py-1"
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      >
                        +
                      </Button>
                      <Button 
                        className="px-2 py-1"
                        onClick={() => removeFromCart(item.id)}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </Card>
              ))}
            </div>

            {cart.length > 0 ? (
              <div className="mt-6">
                <div className="flex justify-between mb-4">
                  <span className="font-medium">Total:</span>
                  <span className="font-medium">${total.toFixed(2)}</span>
                </div>
                <Button className="w-full" onClick={handleCheckout}>
                  Checkout
                </Button>
              </div>
            ) : (
              <p className="text-center mt-8 text-gray-500">Your cart is empty</p>
            )}
          </SheetContent>
        )}
      </Sheet>

      {/* Demo products */}
      <div className="mt-8 grid grid-cols-2 gap-4">
        {products.map(product => (
          <Card key={product.id}>
            <h3 className="font-medium">{product.name}</h3>
            <p className="text-sm text-gray-500">${product.price}</p>
            <Button 
              className="mt-2 w-full"
              onClick={() => addToCart(product)}
            >
              Add to Cart
            </Button>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default ShoppingCartComponent;
