import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Navigation from "./components/home/Navigation"; // Import Navigation
import Home from "./pages/HomePage";
import ProductPage from "./pages/ProductPage";
import ShoppingCartPage from "./pages/ShoppingCartPage";

function App() {
  const [cart, setCart] = useState([]); // Global cart state

  return (
    <BrowserRouter>
      <Navigation /> {/* Navigation is outside Routes, so it appears on all pages */}
      <Routes>
        <Route path="/" element={<Home cart={cart} />} />
        <Route path="/product/:productId" element={<ProductPage cart={cart} setCart={setCart} />} />
        <Route path="/cart" element={<ShoppingCartPage cart={cart} setCart={setCart} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
