import React, { useState } from "react";
import { ShoppingCart, X } from "lucide-react";
import "./ShoppingCartComponent.css"; // Import CSS file

/* --- Custom UI Components --- */

// A styled Button component
const Button = ({ children, className = "", onClick, ...props }) => {
  return (
    <button onClick={onClick} className={`custom-button ${className}`} {...props}>
      {children}
    </button>
  );
};

// A styled Card component
const Card = ({ children, className = "" }) => {
  return <div className={`custom-card ${className}`}>{children}</div>;
};

// A Sheet component to display a sliding panel (like a modal/sidebar)
const Sheet = ({ open, onOpenChange, children }) => {
  return (
    <>
      {open && (
        <>
          {/* Overlay */}
          <div className="overlay" onClick={() => onOpenChange(false)} />
          {children}
        </>
      )}
    </>
  );
};

// Wraps the trigger element that opens the sheet
const SheetTrigger = ({ children, onClick }) => {
  return React.cloneElement(children, { onClick });
};

// The content of the sheet styled as a sidebar
const SheetContent = ({ children }) => {
  return <div className="sheet-content">{children}</div>;
};

const SheetHeader = ({ children }) => {
  return <div className="sheet-header">{children}</div>;
};

const SheetTitle = ({ children }) => {
  return <h2 className="sheet-title">{children}</h2>;
};

/* --- Main ShoppingCartComponent --- */

const ShoppingCartComponent = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [cart, setCart] = useState([]);

  const products = [
    { id: 1, name: "Product 1", price: 19.99 },
    { id: 2, name: "Product 2", price: 29.99 },
  ];

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.id === product.id);
      if (existingItem) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity < 1) return;
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === productId ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="shopping-container">
      {/* Shopping Cart Sidebar */}
      <Sheet open={isOpen} onOpenChange={setIsOpen}>
        <SheetContent>
          <SheetHeader>
            <SheetTitle>Shopping Cart</SheetTitle>
          </SheetHeader>

          <div className="cart-items">
            {cart.map((item) => (
              <Card key={item.id} className="cart-item">
                <div className="cart-item-info">
                  <h3>{item.name}</h3>
                  <p>${item.price.toFixed(2)}</p>
                </div>
                <div className="cart-actions">
                  <Button className="small-btn" onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                    -
                  </Button>
                  <span>{item.quantity}</span>
                  <Button className="small-btn" onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                    +
                  </Button>
                  <Button className="remove-btn" onClick={() => removeFromCart(item.id)}>
                    <X className="icon" />
                  </Button>
                </div>
              </Card>
            ))}
          </div>

          {cart.length > 0 ? (
            <div className="cart-footer">
              <div className="cart-total">
                <span>Total:</span>
                <span>${total.toFixed(2)}</span>
              </div>
              <Button className="checkout-btn">Checkout</Button>
            </div>
          ) : (
            <p className="empty-cart">Your cart is empty</p>
          )}
        </SheetContent>
      </Sheet>

      {/* Cart Trigger */}
      <SheetTrigger onClick={() => setIsOpen(true)}>
        <div className="cart-icon">
          <Button>
            <ShoppingCart />
          </Button>
          {cart.length > 0 && <span className="cart-badge">{cart.length}</span>}
        </div>
      </SheetTrigger>

      {/* Demo Products */}
      <h1 className="heading">Products</h1>
      <div className="product-grid">
        {products.map((product) => (
          <Card key={product.id}>
            <h3>{product.name}</h3>
            <p>${product.price.toFixed(2)}</p>
            <Button className="add-to-cart-btn" onClick={() => addToCart(product)}>
              Add to Cart
            </Button>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default ShoppingCartComponent;
