import React, { useState } from 'react';
import { ShoppingCart, X } from 'lucide-react';

/* --- Custom UI Components --- */

// A styled Button component
const Button = ({ children, className = "", onClick, ...props }) => {
  return (
    <button
      onClick={onClick}
      className={`transition-colors duration-200 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded focus:outline-none ${className}`}
      {...props}
    >
      {children}
    </button>
  );
};

// A styled Card component
const Card = ({ children, className = "" }) => {
  return (
    <div className={`bg-white rounded shadow-md p-6 border border-gray-200 ${className}`}>
      {children}
    </div>
  );
};

// A Sheet component to display a sliding panel (like a modal/sidebar)
const Sheet = ({ open, onOpenChange, children }) => {
  return (
    <>
      {open && (
        <>
          {/* Overlay */}
          <div
            className="fixed inset-0 bg-black opacity-50 z-40"
            onClick={() => onOpenChange(false)}
          />
          {children}
        </>
      )}
    </>
  );
};

// Wraps the trigger element that opens the sheet
const SheetTrigger = ({ children, onClick }) => {
  return React.cloneElement(children, { onClick });
};

// The content of the sheet styled as a sidebar
const SheetContent = ({ children }) => {
  return (
    <div className="fixed top-0 right-0 bottom-0 w-80 bg-white shadow-lg p-6 overflow-auto z-50 transform transition-transform duration-300">
      {children}
    </div>
  );
};

const SheetHeader = ({ children }) => {
  return <div className="mb-4 border-b pb-2">{children}</div>;
};

const SheetTitle = ({ children }) => {
  return <h2 className="text-2xl font-bold text-gray-800">{children}</h2>;
};

/* --- Main ShoppingCartComponent --- */

const ShoppingCartComponent = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [cart, setCart] = useState([]);
  
  // Sample products - replace with your actual products
  const products = [
    { id: 1, name: "Product 1", price: 19.99 },
    { id: 2, name: "Product 2", price: 29.99 }
  ];

  const addToCart = (product) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity < 1) return;
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId 
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
    try {
      // Replace with your actual Stripe publishable key
      const stripe = window.Stripe('your_publishable_key');
      
      // Replace with your actual backend endpoint
      const response = await fetch('/api/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ items: cart }),
      });
      
      const session = await response.json();
      
      // Redirect to Stripe Checkout
      const result = await stripe.redirectToCheckout({
        sessionId: session.id,
      });

      if (result.error) {
        console.error(result.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="relative p-6 bg-gray-100 min-h-screen">
      {/* Sheet for the Shopping Cart */}
      <Sheet open={isOpen} onOpenChange={setIsOpen}>
        <SheetContent>
          <SheetHeader>
            <SheetTitle>Shopping Cart</SheetTitle>
          </SheetHeader>
          
          <div className="space-y-4">
            {cart.map(item => (
              <Card key={item.id} className="flex flex-col sm:flex-row sm:justify-between items-center">
                <div>
                  <h3 className="font-medium text-lg text-gray-800">{item.name}</h3>
                  <p className="text-sm text-gray-500">${item.price.toFixed(2)}</p>
                </div>
                <div className="flex items-center gap-2 mt-4 sm:mt-0">
                  <Button 
                    className="bg-blue-400 hover:bg-blue-500 px-3 py-1"
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                  >
                    -
                  </Button>
                  <span className="text-gray-700 font-medium">{item.quantity}</span>
                  <Button 
                    className="bg-blue-400 hover:bg-blue-500 px-3 py-1"
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                  >
                    +
                  </Button>
                  <Button 
                    className="bg-red-500 hover:bg-red-600 px-3 py-1"
                    onClick={() => removeFromCart(item.id)}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              </Card>
            ))}
          </div>

          {cart.length > 0 ? (
            <div className="mt-6">
              <div className="flex justify-between mb-4 border-t pt-4">
                <span className="font-semibold text-gray-800">Total:</span>
                <span className="font-semibold text-gray-800">${total.toFixed(2)}</span>
              </div>
              <Button className="w-full" onClick={handleCheckout}>
                Checkout
              </Button>
            </div>
          ) : (
            <p className="text-center mt-8 text-gray-500">Your cart is empty</p>
          )}
        </SheetContent>
      </Sheet>

      {/* Cart Trigger */}
      <div className="flex justify-end">
        <SheetTrigger onClick={() => setIsOpen(true)}>
          <div className="relative inline-block">
            <Button>
              <ShoppingCart className="h-5 w-5" />
            </Button>
            {cart.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs">
                {cart.length}
              </span>
            )}
          </div>
        </SheetTrigger>
      </div>

      {/* Demo Products */}
      <h1 className="text-3xl font-bold text-gray-800 mb-6 mt-8">Products</h1>
      <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 md:grid-cols-3">
        {products.map(product => (
          <Card key={product.id}>
            <h3 className="font-semibold text-xl text-gray-800">{product.name}</h3>
            <p className="text-gray-500 mt-2">${product.price.toFixed(2)}</p>
            <Button 
              className="mt-4 w-full"
              onClick={() => addToCart(product)}
            >
              Add to Cart
            </Button>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default ShoppingCartComponent;
