import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { productId } = useParams();

  // Sample product data (you may want to fetch it dynamically)
  const products = [
    { id: 1, title: "RWK Black T-Shirt", description: "A cool black t-shirt", image: "/path/to/image1.png", price: "$25", sizes: ["S", "M", "L", "XL"] },
    { id: 2, title: "RWK White T-Shirt", description: "A cool white t-shirt", image: "/path/to/image2.png", price: "$25", sizes: ["S", "M", "L"] },
    // Add more products here
  ];

  // Find the selected product based on the productId from the URL
  const product = products.find(p => p.id === parseInt(productId));

  // State for size and quantity
  const [selectedSize, setSelectedSize] = useState(product?.sizes[0] || "S");
  const [quantity, setQuantity] = useState(1);

  // Handle size change
  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  };

  // Handle quantity change (ensuring it's always a positive number)
  const handleQuantityChange = (event) => {
    const newQuantity = parseInt(event.target.value);
    if (newQuantity > 0) {
      setQuantity(newQuantity);
    }
  };

  return (
    <div className="product-page">
      {product ? (
        <>
          <h2>{product.title}</h2>
          <img src={product.image} alt={product.title} />
          <p>{product.description}</p>
          <p>{product.price}</p>

          {/* Size Selection */}
          <div className="product-size">
            <label htmlFor="size">Select Size: </label>
            <select
              id="size"
              value={selectedSize}
              onChange={handleSizeChange}
            >
              {product.sizes.map((size, index) => (
                <option key={index} value={size}>
                  {size}
                </option>
              ))}
            </select>
          </div>

          {/* Quantity Selection */}
          <div className="product-quantity">
            <label htmlFor="quantity">Quantity: </label>
            <input
              type="number"
              id="quantity"
              value={quantity}
              onChange={handleQuantityChange}
              min="1"
            />
          </div>

          <button className="button">Buy Now</button>
        </>
      ) : (
        <p>Product not found!</p>
      )}
    </div>
  );
};

export default ProductPage;
